$(document).ready(function () {

  $(".mask-phone").mask("+7 (999) 999-99-99");

  // add-open-class
  $('.burger-menu').click(function(){
   if($(this).parent().is('.menu-burger--opened')){
     $(this).parent().removeClass('menu-burger--opened');
     $('body').removeClass('menu-open-wrapper-page');
   }else{
     $(this).parent().addClass('menu-burger--opened');
     $('body').addClass('menu-open-wrapper-page');
   }
  });
  
// add-open-class
  $('.menu-item__link--dd-open').click(function(){
   if($(this).parent().is('.menu-item--dd-opened')){
    $(this).parent().removeClass('menu-item--dd-opened');
   }else{
    $(this).parent().addClass('menu-item--dd-opened');
   }
  });

  //slider
  $('.m-slider').slick({
    slideToShow: 1,
    slidesToScroll: 1,
    nextArrow: '<button type="button" class="slide-next"></button>',
    prevArrow: '<button type="button" class="slide-prev"></button>',
    mobileFirst: true,
    responsive: [
    {
      breakpoint: 767,
      settings: {
        slidesToShow: 2
      }
    },
    {
      breakpoint: 979,
      settings: {
        slidesToShow: 3
      }
    }
  ]
  });


  //modal
  $('.open-modal').magnificPopup({
        type: 'inline',
        showCloseBtn: false
    });

  $('.modal-close').click(function(){
    $.magnificPopup.close();
  });


  // progress bar
  const barValues = [9, 2, 6, 8];
  const barItems = document.querySelectorAll('.progress-bar__item');

  let counterValue = 0;

  barItems.forEach(item => {
    let barValue = barValues[counterValue];

    item.querySelector('.number').textContent = barValue;

    if (barValue > 3 && barValue < 7) {
      item.classList.add('bar_normal');
    } else if (barValue >= 7) {
      item.classList.add('bar_danger');
    }

    item.querySelector('.progress-bar__line').innerHTML = createItems(barValue);

    createItems(barValue);

    counterValue++;
  });


  function createItems(barValue) {
    let elementsString = '';

    for (let i = 0; i < barValue; i++) {
      if (i >= 16) {
        break;
      }

      elementsString += '<div></div>';
    }

    return elementsString;
  }

//scroll up
$("a.scrollto").click(function () {
    var elementClick = $(this).attr("href")
    var destination = $(elementClick).offset().top;
    jQuery("html:not(:animated),body:not(:animated)").animate({scrollTop: destination}, 800);
    return false;
});

});

